import React, { Component } from 'react'
import Container from 'react-bootstrap/Container'
import TeamServices from '../../../services/team.services'
import './match.css'
import UserCard from '../user/UserCard'

import Select from '../../ui/Select'
import Row from 'react-bootstrap/Row'
import Col from 'react-bootstrap/Col'
import Button from 'react-bootstrap/Button'


class Match extends Component {


    constructor(props) {
        super(props)
        this.state = { team: {}, selectedTeam: null }
        this.services = new TeamServices()
    }

    componentDidMount = () => this.getAllTeam()

    getAllTeam = () => {
        this.services.getAllTeam()
            .then(allteam => this.setState({ team: allteam }))
            .catch(err => console.log(err))
    }
    getMyTeam = () => {
        this.services.getMyTeam()
            .then(theTeam => this.setState({ team: theTeam }))
            .catch(err => console.log(err))
    }
    selectedTeam = () => {
        this.services.selectedTeam()
            .then(membersTeam => this.setState({ selectedTeam: membersTeam }))
            .catch(err => console.log(err))
    }

    num = () => {
        Math.floor((Math.random() * (11 - 5)) + 5);
    }

    render() {
        console.log(this.props.selectedTeam)
        return (
            <Container>
                <Row>
                    <Col><h3 className="h3-style">Selecciona tu equipo</h3>
                        <Select teams={this.state.team} />
                    </Col>
                    <Col><h3>Selecciona el equipo del contrincante</h3>
                        <Select teams={this.state.team} />
                    </Col>
                </Row>
                <Row>
                    <Col>
                        <Button className="button-style" onClick={this.num}> Generar c√≥digo de partida</Button>
                    </Col>
                </Row>
                <Col>

                    {this.props.selectedTeam && this.props.selectedTeam.members.map(elm => <UserCard key={elm._id} {...elm} />)}
                </Col>
            </Container >
        )
    }
}







export default Match
