import React, { Component } from 'react'

import ProServices from '../../../services/team.services'

import ProForm from '../proForm/ProForm'
import ProCard from './TeamCard'

import Container from 'react-bootstrap/Container'
import Row from 'react-bootstrap/Row'
import Button from 'react-bootstrap/Button'
import Modal from 'react-bootstrap/Modal'

class TeamList extends Component {

    constructor(props) {
        super(props)
        this.state = {
            team: [],
            showmodal: false
        }
        this.services = new TeamServices()
    }

    componentDidMount = () => this.getAllTeam()

    getAllTeam = () => {
        this.services.getAllTeam()
            .then(allTeam => this.setState({ pro: allteam }))
            .catch(err => console.log(err))
    }

    closeModal = () => this.setState({ showmodal: false })
    openModal = () => this.setState({ showmodal: true })

    render() {

        return (
            <Container>

                <h1>Profesionales</h1>

                {this.props.loggedInUser && <Button className="mb-20" variant="dark" onClick={this.openModal}>AÃ±adir jugador Pro</Button>}

                {this.state.pro.length ? (
                    <Row>
                        {this.state.pro.map(elm => <TeamCard key={elm._id} {...elm} />)}
                    </Row>
                )
                    :
                    <p>CARGANDO...</p>

                }






                <Modal show={this.state.showmodal} onHide={this.closeModal}>
                    <Modal.Body>
                        <h3>Nuevo Profesional</h3>
                        <hr></hr>
                        <ProForm closeModal={this.closeModal} refreshList={this.getAllTeam} />
                    </Modal.Body>
                </Modal>

            </Container>
        )
    }
}

export default TeamList
