import axios from 'axios'

export default class Services {

    constructor() {
        this.service = axios.create({
            baseURL: 'http://localhost:5000/pro',
            withCredentials: true
        })
    }

    getAllPros = () => this.service.get('/getAllPros').then(response => response.data)
    getProDetails = id => this.service.get(`/getOnePro/${id}`).then(response => response.data)
    postPro = pro => this.service.post(`/new`, pro).then(response => response.data)
}
